var button=document.querySelector(".btn-search"),popup=document.querySelector(".appointment-form"),checkin=document.querySelector("#check-in-date"),departure=document.querySelector("#date-of-departure"),adults=document.querySelector("#adults");button.addEventListener("click",function(e){e.preventDefault(),popup.classList.toggle("appointment-form-close")}),popup.addEventListener("submit",function(e){checkin.value&&departure.value&&adults.value||(e.preventDefault(),alert("Нужно ввести даты заезда и выезда, и количество людей"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("appointment-form")&&popup.classList.toggle("appointment-form-close")});
